userSchema.methods.checkPassword = async function (password) {
  try {
    const match = await bcrypt.compare(password, this.password);
    if (match) {
      return Promise.resolve();
    }
    throw new Error("0");
  } catch (error) {
    return Promise.reject(error.message);
  }
};

